import React,{useState} from "react";
import food from "../csv2jsondata"

function Calcalorie(){
    const [searchInput,setSearch]=useState("changing...");
    function search(event){
        const searchInput=event.target.value;
        // setSearch(food.filter(food))
        // setSearch(searchInput)
        if (searchInput.length > 0){
                const searchval= food.filter((foods)=>{
                return (foods.Food.toLowerCase().match(searchInput))    
                })
                // const arr=foods.Food.toUpperCase().match(searchInput)
                // return Object.values(foods).join('').toLowerCase().includes(searchInput.toLowerCase())
                // console.log(for each searchval.);
                // searchval.forEach((val)=>{setSearch(val.Food);})
                let count=0;
                    setSearch(searchval.map((data)=>{ 
                        count++;
                        return (<div id={count} onClick={update}>{data.Food}</div>
                                 )}))
                // return (arr.forEach((data)=> {return setSearch(data)}))})
                    // setSearch(searchval.Food);
            }
    }
    function update(){
        console.log(document.getElementById(1).innerHTML)
        document.getElementById("f").value={searchInput};
}
    
      return(
            
            (<div>
                <form>
                Food:<input id="f" type="search" onChange={search} name="food" placeholder="search..." />
                <div id="foodsdiv" >{searchInput}</div> 
               
                <input type="submit" />

                </form>

      </div>))
}
export default Calcalorie ;
