import React,{useState} from "react";
import food from "../csv2jsondata"

function Calcalorie(){
    const [searchInput,setSearch]=useState("changing...");
    const [displaySearch,setDisplaySearch]=useState("block");
    function search(event){
        // setDisplaySearch(displaySearch);
        // console.log(event)
        const searchInput=event.target.value;
        if (searchInput.length > 0){
            document.getElementById("foodsdiv").style.setProperty('display' , {displaySearch})
             setDisplaySearch(displaySearch); 
                const searchval= food.filter((foods)=>{
                return (foods.Food.toLowerCase().match(searchInput))    
                })
                let count=0;
                    setSearch(searchval.map((data)=>{ 
                        count++;
                        return (<div id={count} className="forcsshand" onClick={update}>{data.Food}</div> )}))
            }
    }
    
    //for updating selected value into input search box
    function update(e){
        document.getElementById("f").value=e.target.innerHTML;
        displaySearch="None"
        document.getElementById("foodsdiv").style.setProperty('display' , {displaySearch})  
    }
    
      return(
            
            (<div>
                <form>
                Food:<input id="f" type="search" onChange={search} name="food" placeholder="search..." />
                <input type="submit" />
                <div id="foodsdiv" style= "display : {displaySearch} ; ">{searchInput}</div> 
               
                

                </form>

      </div>))
}
export default Calcalorie ;
